mapper.select:
SELECT A.LEV , A.BOARD_IDX , A.BOARD_KEY , A.PARENT_KEY , A.TITLE , A.NAME , A.CONTENTS , A.HIT_CNT , A.CHK_INFO , A.DEL_YN , A.PC_DT FROM ( SELECT '1' AS LEV , T1.BOARD_IDX , T1.BOARD_KEY , T1.PARENT_KEY , T1.TITLE , (CASE WHEN T1.USER_KEY <> '' THEN ( SELECT T2.USER_NAME FROM TB_MEMBER T2 WHERE 1 = 1 AND T1.USER_KEY = T2.USER_KEY ) ELSE T1.NAME END) AS NAME , T1.CONTENTS , T1.HIT_CNT , T1.CHK_INFO , T1.DEL_YN , DATE_FORMAT(T1.PC_DT, '%Y-%m-%d') AS PC_DT FROM TB_BOARD T1 WHERE 1 = 1 AND T1.CHK_INFO = '1' AND T1.DEL_YN = 'N' AND T1.BOARD_DVSN = #{BOARD_DVSN}
UNION
SELECT '2' AS LEV , T1.BOARD_IDX , T1.BOARD_KEY , T1.PARENT_KEY , T1.TITLE , (CASE WHEN T1.USER_KEY <> '' THEN ( SELECT T2.USER_NAME FROM TB_MEMBER T2 WHERE 1 = 1 AND T1.USER_KEY = T2.USER_KEY ) ELSE T1.NAME END) AS NAME , T1.CONTENTS , T1.HIT_CNT , T1.CHK_INFO , T1.DEL_YN , DATE_FORMAT(T1.PC_DT, '%Y-%m-%d') AS PC_DT FROM TB_BOARD T1 WHERE 1 = 1 AND T1.BOARD_DVSN = #{BOARD_DVSN} ) A WHERE 1 = 1 ORDER BY A.LEV ASC , A.BOARD_IDX DESC

mapper.update:

	UPDATE TB_BOARD_NOTICE
	SET
		USER_KEY = #{USER_KEY}
	  , TITLE = #{TITLE}
	  , NAME = #{NAME}
	  , CONTENTS = #{CONTENTS}
	  , CHK_INFO = IFNULL(#{CHK_INFO}, '0')
	  , LUPD_CNT = IFNULL(LUPD_CNT, 0) + 1
	  , PC_TS = CURRENT_TIMESTAMP
	WHERE 1 = 1
	AND IDX = #{IDX}
	AND DEL_YN = 'N'
