mapper.update:

	UPDATE TB_MEMBER
	SET
		USER_NAME = #{USER_NAME}
	  , USER_AGE = #{USER_AGE}
	  , BIRTH = DATE_FORMAT(IFNULL(#{BIRTH}, now()), '%Y-%m-%d')
	  , USER_MAIL = #{USER_MAIL}
	  , PHONE_NUMBER = #{PHONE_NUMBER}
	  , GENDER = #{GENDER}
	  , RGS_DT = DATE_FORMAT(IFNULL(#{RGS_DT}, now()), '%Y-%m-%d')
	  , RGS_CNT = IFNULL(#{RGS_CNT}, 1)
	  , OUTL_CTT = #{OUTL_CTT}
	  , CHK01 = IFNULL(#{CHK01}, '0')
	  , CHK02 = IFNULL(#{CHK02}, '0')
	  , CHK03 = IFNULL(#{CHK03}, '0')
	  , CHK04 = IFNULL(#{CHK04}, '0')
	  , LUPD_CNT = IFNULL(LUPD_CNT, 0) + 1
	  , PC_TS = CURRENT_TIMESTAMP
	WHERE 1 = 1
	AND USER_KEY = #{USER_KEY}
	AND DEL_YN = 'N'


	UPDATE TB_BOARD_NOTICE
	SET
		USER_KEY = #{USER_KEY}
	  , TITLE = #{TITLE}
	  , NAME = #{NAME}
	  , CONTENTS = #{CONTENTS}
	  , CHK_INFO = IFNULL(#{CHK_INFO}, '0')
	  , LUPD_CNT = IFNULL(LUPD_CNT, 0) + 1
	  , PC_TS = CURRENT_TIMESTAMP
	WHERE 1 = 1
	AND IDX = #{IDX}
	AND DEL_YN = 'N'
