mapper.update:

	UPDATE TB_MEMBER
	SET
		USER_NAME = #{USER_NAME}
	  , USER_AGE = #{USER_AGE}
	  , BIRTH = DATE_FORMAT(IFNULL(#{BIRTH}, now()), '%Y-%m-%d')
	  , E_MAIL = #{E_MAIL}
	  , PHONE_NUMBER = #{PHONE_NUMBER}
	  , GENDER = #{GENDER}
	  , REGISTRATION_DATE = DATE_FORMAT(IFNULL(#{REGISTRATION_DATE}, now()), '%Y-%m-%d')
	  , COUNTING = IFNULL(#{COUNTING}, 1)
	  , OUTL_CTT = #{OUTL_CTT}
	  , CHK01 = IFNULL(#{CHK01}, '0')
	  , CHK02 = IFNULL(#{CHK02}, '0')
	  , CHK03 = IFNULL(#{CHK03}, '0')
	  , CHK04 = IFNULL(#{CHK04}, '0')
	  , LUPD_CNT = IFNULL(LUPD_CNT, 0) + 1
	  , PC_TS = CURRENT_TIMESTAMP
	WHERE 1 = 1
	AND USER_KEY = #{USER_KEY}
